import React from "react";
import { useScrollToTop } from "react-hooks-lab";

const ScrollToTop = () => {
  useScrollToTop();

  useEffect(() => {
    // your stuff
  }, []);
};

import React from "react";

const ScrollToTop = ({ children, location: { pathname } }) => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  return children || null;
};

export default withRouter(ScrollToTop);

